export * from './mocks/intersection-observer';
export * from './mocks/resize-observer';
export * from './mocks/size';
export * from './mocks/viewport';
export * from './mocks/web-animations-api';
const protectedProps = ['_x', '_y', '_width', '_height'];

class MockedDOMRectReadOnly implements DOMRectReadOnly {
  _x: number;
  _y: number;
  _width: number;
  _height: number;

  constructor(x = 0, y = 0, width = 0, height = 0) {
    this._x = x;
    this._y = y;
    this._width = width;
    this._height = height;

    // make protected props non-enumerable
    protectedProps.forEach((prop) => {
      const descriptor = Object.getOwnPropertyDescriptor(this, prop);

      if (descriptor) {
        Object.defineProperty(this, prop, {
          ...descriptor,
          enumerable: false,
        });
      }
    });
  }

  get x() {
    return this._x;
  }

  set x(_value) {
    // repeat native behavior
  }

  get y() {
    return this._y;
  }

  set y(_value) {
    // repeat native behavior
  }

  get width() {
    return this._width;
  }

  set width(_value) {
    // repeat native behavior
  }

  get height() {
    return this._height;
  }

  set height(_value) {
    // repeat native behavior
  }

  get left() {
    return this._x;
  }

  set left(_value) {
    // repeat native behavior
  }

  get right() {
    return this._x + Math.max(0, this._width);
  }

  set right(_value) {
    // repeat native behavior
  }

  get top() {
    return this._y;
  }

  set top(_value) {
    // repeat native behavior
  }

  get bottom() {
    return this._y + Math.max(0, this._height);
  }

  set bottom(_value) {
    // repeat native behavior
  }

  toJSON() {
    return {
      bottom: this.bottom,
      height: this.height,
      left: this.left,
      right: this.right,
      top: this.top,
      width: this.width,
      x: this.x,
      y: this.y,
    };
  }

  toString() {
    return '[object DOMRectReadOnly]';
  }
}

export class MockedDOMRect extends MockedDOMRectReadOnly implements DOMRect {
  constructor(x = 0, y = 0, width = 0, height = 0) {
    super(x, y, width, height);
  }

  get x() {
    return super.x;
  }

  set x(_value: number) {
    this._x = _value;
  }

  get y() {
    return super.y;
  }

  set y(_value: number) {
    this._y = _value;
  }

  get width() {
    return super.width;
  }

  set width(_value: number) {
    this._width = _value;
  }

  get height() {
    return super.height;
  }

  set height(_value: number) {
    this._height = _value;
  }

  toString() {
    return '[object DOMRect]';
  }
}

if (typeof DOMRectReadOnly === 'undefined') {
  Object.defineProperty(window, 'DOMRectReadOnly', {
    writable: true,
    configurable: true,
    value: MockedDOMRectReadOnly,
  });
}

if (typeof DOMRect === 'undefined') {
  Object.defineProperty(window, 'DOMRect', {
    writable: true,
    configurable: true,
    value: MockedDOMRect,
  });
}
import { Writable, PartialDeep } from 'type-fest';
import './size/DOMRect';

export type IntersectionDescription = Omit<
  PartialDeep<Writable<IntersectionObserverEntry>>,
  'target'
> & {
  target?: Element;
};

export type NodeIntersectionDescription = {
  node: HTMLElement;
  desc?: IntersectionDescription;
};

type State = {
  observers: MockedIntersectionObserver[];
};

const defaultState: State = {
  observers: [],
};

const state = { ...defaultState };

function isElement(tested: unknown): tested is HTMLElement {
  return typeof HTMLElement === 'object'
    ? tested instanceof HTMLElement // DOM2
    : Boolean(tested) &&
        typeof tested === 'object' &&
        tested !== null &&
        (tested as HTMLElement).nodeType === 1 &&
        typeof (tested as HTMLElement).nodeName === 'string';
}

function getObserversByNode(node: HTMLElement) {
  return state.observers.filter((observer) => observer.nodes.includes(node));
}

function normalizeNodeDescriptions(
  nodeDescriptions: (NodeIntersectionDescription | HTMLElement)[]
): NodeIntersectionDescription[] {
  return nodeDescriptions.map((nodeDesc) => {
    if (isElement(nodeDesc)) {
      return { node: nodeDesc };
    }

    return nodeDesc;
  });
}

function getNodeDescriptionsByObserver(
  nodeDescriptions: NodeIntersectionDescription[]
) {
  const observerNodes: {
    observer: MockedIntersectionObserver;
    nodeDescriptions: NodeIntersectionDescription[];
  }[] = [];

  nodeDescriptions.forEach(({ node, desc }) => {
    const observers = getObserversByNode(node);

    observers.forEach((observer) => {
      const observerNode = observerNodes.find(
        ({ observer: obs }) => obs === observer
      );

      if (observerNode) {
        observerNode.nodeDescriptions.push({ node, desc });
      } else {
        observerNodes.push({
          observer,
          nodeDescriptions: [{ node, desc }],
        });
      }
    });
  });

  return observerNodes;
}

function findNodeIndex(nodes: HTMLElement[], node: HTMLElement) {
  const index = nodes.findIndex((nodeInArray) => node.isSameNode(nodeInArray));

  if (index === -1) {
    throw new Error('IntersectionObserver mock: node not found');
  }

  return index;
}

export class MockedIntersectionObserver implements IntersectionObserver {
  nodes: HTMLElement[] = [];
  nodeStates: IntersectionObserverEntry[] = [];
  callback: IntersectionObserverCallback;
  readonly root: Element | Document | null = null;
  readonly rootMargin: string = '0px 0px 0px 0px';
  readonly thresholds: ReadonlyArray<number> = [0];
  timeOrigin = 0;

  constructor(
    callback: IntersectionObserverCallback,
    options?: IntersectionObserverInit | undefined
  ) {
    this.callback = callback;

    if (options) {
      if (typeof options.root !== 'undefined') {
        this.root = options.root;
      }

      if (typeof options.rootMargin !== 'undefined') {
        this.rootMargin = options.rootMargin;
      }

      if (typeof options.threshold !== 'undefined') {
        this.thresholds = Array.isArray(options.threshold)
          ? options.threshold
          : [options.threshold];
      }
    }

    this.timeOrigin = performance.now();

    state.observers.push(this);
  }

  observe(node: HTMLElement) {
    this.nodes.push(node);
    this.nodeStates.push({
      isIntersecting: false,
      target: node,
      time: performance.now() - this.timeOrigin,
      rootBounds: new DOMRectReadOnly(),
      intersectionRect: new DOMRectReadOnly(),
      intersectionRatio: 0,
      boundingClientRect: new DOMRectReadOnly(),
    });
  }

  unobserve(node: HTMLElement) {
    const index = this.nodes.findIndex((value) => value.isSameNode(node));

    this.nodes.splice(index, 1);
    this.nodeStates.splice(index, 1);
  }

  disconnect() {
    this.nodes = [];
    this.nodeStates = [];
  }

  triggerNode(node: HTMLElement, desc: IntersectionDescription) {
    const index = findNodeIndex(this.nodes, node);
    const nodeState = this.nodeStates[index];

    this.nodeStates[index] = {
      ...nodeState,
      time: performance.now() - this.timeOrigin,
      ...desc,
    } as IntersectionObserverEntry;

    this.callback([this.nodeStates[index]], this);
  }

  triggerNodes(nodeDescriptions: NodeIntersectionDescription[]) {
    const nodeIndexes = nodeDescriptions.map(({ node }) =>
      findNodeIndex(this.nodes, node)
    );

    const nodeStates = nodeDescriptions.map(({ desc }, index) => {
      const newState = {
        ...this.nodeStates[nodeIndexes[index]],
        time: performance.now() - this.timeOrigin,
        ...desc,
      } as IntersectionObserverEntry;

      this.nodeStates[nodeIndexes[index]] = newState;

      return newState;
    });

    this.callback(nodeStates, this);
  }

  takeRecords(): IntersectionObserverEntry[] {
    return [];
  }
}

function mockIntersectionObserver() {
  const savedImplementation = window.IntersectionObserver;

  Object.defineProperty(window, 'IntersectionObserver', {
    writable: true,
    configurable: true,
    value: MockedIntersectionObserver,
  });

  afterAll(() => {
    window.IntersectionObserver = savedImplementation;
  });

  return {
    enterAll: (desc?: IntersectionDescription) => {
      state.observers.forEach((observer) => {
        const nodeDescriptions = observer.nodes.map((node) => ({
          node,
          desc: {
            intersectionRatio: 1,
            ...desc,
            isIntersecting: true,
          },
        }));

        observer.triggerNodes(nodeDescriptions);
      });
    },
    enterNode: (node: HTMLElement, desc?: IntersectionDescription) => {
      const observers = getObserversByNode(node);

      observers.forEach((observer) => {
        observer.triggerNode(node, {
          intersectionRatio: 1,
          ...desc,
          isIntersecting: true,
        });
      });
    },
    enterNodes: (
      nodeDescriptions: (NodeIntersectionDescription | HTMLElement)[]
    ) => {
      const normalizedNodeDescriptions =
        normalizeNodeDescriptions(nodeDescriptions);
      const observerNodes = getNodeDescriptionsByObserver(
        normalizedNodeDescriptions
      );

      observerNodes.forEach(({ observer, nodeDescriptions }) => {
        observer.triggerNodes(
          nodeDescriptions.map(({ node, desc }) => ({
            node,
            desc: { intersectionRatio: 1, ...desc, isIntersecting: true },
          }))
        );
      });
    },
    leaveAll: (desc?: IntersectionDescription) => {
      state.observers.forEach((observer) => {
        const nodeDescriptions = observer.nodes.map((node) => ({
          node,
          desc: {
            intersectionRatio: 0,
            ...desc,
            isIntersecting: false,
          },
        }));

        observer.triggerNodes(nodeDescriptions);
      });
    },
    leaveNode: (node: HTMLElement, desc?: IntersectionDescription) => {
      const observers = getObserversByNode(node);

      observers.forEach((observer) => {
        observer.triggerNode(node, {
          intersectionRatio: 0,
          ...desc,
          isIntersecting: false,
        });
      });
    },
    triggerNodes: (
      nodeDescriptions: (NodeIntersectionDescription | HTMLElement)[]
    ) => {
      const normalizedNodeDescriptions =
        normalizeNodeDescriptions(nodeDescriptions);

      const observerNodes = getNodeDescriptionsByObserver(
        normalizedNodeDescriptions
      );

      observerNodes.forEach(({ observer, nodeDescriptions }) => {
        observer.triggerNodes(nodeDescriptions);
      });
    },
    leaveNodes: (
      nodeDescriptions: (NodeIntersectionDescription | HTMLElement)[]
    ) => {
      const normalizedNodeDescriptions =
        normalizeNodeDescriptions(nodeDescriptions);

      const observerNodes = getNodeDescriptionsByObserver(
        normalizedNodeDescriptions
      );

      observerNodes.forEach(({ observer, nodeDescriptions }) => {
        observer.triggerNodes(
          nodeDescriptions.map(({ node, desc }) => ({
            node,
            desc: { intersectionRatio: 0, ...desc, isIntersecting: false },
          }))
        );
      });
    },
    cleanup: () => {
      window.IntersectionObserver = savedImplementation;

      state.observers = [];
    },
  };
}

export { mockIntersectionObserver };
import { RequireAtLeastOne } from 'type-fest';

type Sizes = {
  borderBoxSize: ResizeObserverSize[];
  contentBoxSize: ResizeObserverSize[];
  contentRect: DOMRectReadOnly;
};

type ResizeObserverSizeInput = RequireAtLeastOne<ResizeObserverSize>;
type SizeInput = {
  borderBoxSize: ResizeObserverSizeInput[] | ResizeObserverSizeInput;
  contentBoxSize: ResizeObserverSizeInput[] | ResizeObserverSizeInput;
};

type Size = RequireAtLeastOne<SizeInput>;

type State = {
  observers: MockedResizeObserver[];
  targetObservers: Map<HTMLElement, MockedResizeObserver[]>;
  elementSizes: Map<HTMLElement, Sizes>;
};

const state: State = {
  observers: [],
  targetObservers: new Map(),
  elementSizes: new Map(),
};

function resetState() {
  state.observers = [];
  state.targetObservers = new Map();
  state.elementSizes = new Map();
}

function defineResizeObserverSize(
  input: ResizeObserverSizeInput
): ResizeObserverSize {
  return {
    blockSize: input.blockSize ?? 0,
    inlineSize: input.inlineSize ?? 0,
  };
}

class MockedResizeObserver implements ResizeObserver {
  callback: ResizeObserverCallback;
  observationTargets = new Set<HTMLElement>();
  activeTargets = new Set<HTMLElement>();

  constructor(callback: ResizeObserverCallback) {
    this.callback = callback;
    state.observers.push(this);
  }

  observe = (node: HTMLElement) => {
    this.observationTargets.add(node);
    this.activeTargets.add(node);

    if (state.targetObservers.has(node)) {
      // eslint-disable-next-line @typescript-eslint/no-non-null-assertion
      state.targetObservers.get(node)!.push(this);
    } else {
      state.targetObservers.set(node, [this]);
    }
  };

  unobserve = (node: HTMLElement) => {
    this.observationTargets.delete(node);

    const targetObservers = state.targetObservers.get(node);

    if (targetObservers) {
      const index = targetObservers.findIndex((mro) => mro === this);

      targetObservers.splice(index, 1);

      if (targetObservers.length === 0) {
        state.targetObservers.delete(node);
      }
    }
  };

  disconnect = () => {
    this.observationTargets.clear();

    for (const node of this.observationTargets) {
      const targetObservers = state.targetObservers.get(node);

      if (targetObservers) {
        const index = targetObservers.findIndex((mro) => mro === this);

        targetObservers.splice(index, 1);

        if (targetObservers.length === 0) {
          state.targetObservers.delete(node);
        }
      }
    }
  };
}

function elementToEntry(element: HTMLElement): ResizeObserverEntry | null {
  const boundingClientRect = element.getBoundingClientRect();
  let sizes = state.elementSizes.get(element);

  if (!sizes) {
    sizes = {
      borderBoxSize: [
        {
          blockSize: boundingClientRect.width,
          inlineSize: boundingClientRect.height,
        },
      ],
      contentBoxSize: [
        {
          blockSize: boundingClientRect.width,
          inlineSize: boundingClientRect.height,
        },
      ],
      contentRect: boundingClientRect,
    };
  }

  if (sizes.contentRect.width === 0 && sizes.contentRect.height === 0) {
    return null;
  }

  return {
    borderBoxSize: Object.freeze(sizes.borderBoxSize),
    contentBoxSize: Object.freeze(sizes.contentBoxSize),
    contentRect: sizes.contentRect,
    devicePixelContentBoxSize: Object.freeze(
      sizes.contentBoxSize.map((size) => ({
        blockSize: size.blockSize * window.devicePixelRatio,
        inlineSize: size.inlineSize * window.devicePixelRatio,
      }))
    ),
    target: element,
  };
}

function mockResizeObserver() {
  const savedImplementation = window.ResizeObserver;

  Object.defineProperty(window, 'ResizeObserver', {
    writable: true,
    configurable: true,
    value: MockedResizeObserver,
  });

  afterEach(() => {
    resetState();
  });

  afterAll(() => {
    window.ResizeObserver = savedImplementation;
  });

  return {
    getObservers: (element?: HTMLElement) => {
      if (element) {
        return [...(state.targetObservers.get(element) ?? [])];
      }

      return [...state.observers];
    },
    getObservedElements: (observer?: ResizeObserver) => {
      if (observer) {
        return [...(observer as MockedResizeObserver).observationTargets];
      }

      return [...state.targetObservers.keys()];
    },
    mockElementSize: (element: HTMLElement, size: Size) => {
      let contentBoxSize: ResizeObserverSize[];
      let borderBoxSize: ResizeObserverSize[];

      if (!size.borderBoxSize && size.contentBoxSize) {
        if (!Array.isArray(size.contentBoxSize)) {
          size.contentBoxSize = [size.contentBoxSize];
        }

        contentBoxSize = size.contentBoxSize.map(defineResizeObserverSize);
        borderBoxSize = contentBoxSize;
      } else if (size.borderBoxSize && !size.contentBoxSize) {
        if (!Array.isArray(size.borderBoxSize)) {
          size.borderBoxSize = [size.borderBoxSize];
        }

        contentBoxSize = size.borderBoxSize.map(defineResizeObserverSize);
        borderBoxSize = contentBoxSize;
      } else if (size.borderBoxSize && size.contentBoxSize) {
        if (!Array.isArray(size.borderBoxSize)) {
          size.borderBoxSize = [size.borderBoxSize];
        }

        if (!Array.isArray(size.contentBoxSize)) {
          size.contentBoxSize = [size.contentBoxSize];
        }

        contentBoxSize = size.contentBoxSize.map(defineResizeObserverSize);
        borderBoxSize = size.borderBoxSize.map(defineResizeObserverSize);

        if (borderBoxSize.length !== contentBoxSize.length) {
          throw new Error(
            'Both borderBoxSize and contentBoxSize must have the same amount of elements.'
          );
        }
      } else {
        throw new Error(
          'Neither borderBoxSize nor contentBoxSize was provided.'
        );
      }

      // verify contentBoxSize and borderBoxSize are not negative
      contentBoxSize.forEach((size, index) => {
        if (size.blockSize < 0) {
          throw new Error(
            `contentBoxSize[${index}].blockSize must not be negative.`
          );
        }

        if (size.inlineSize < 0) {
          throw new Error(
            `contentBoxSize[${index}].inlineSize must not be negative.`
          );
        }
      });

      borderBoxSize.forEach((size, index) => {
        if (size.blockSize < 0) {
          throw new Error(
            `borderBoxSize[${index}].blockSize must not be negative.`
          );
        }

        if (size.inlineSize < 0) {
          throw new Error(
            `borderBoxSize[${index}].inlineSize must not be negative.`
          );
        }
      });

      const contentRect = new DOMRect(
        0,
        0,
        contentBoxSize.reduce((acc, size) => acc + size.inlineSize, 0),
        contentBoxSize.reduce((acc, size) => acc + size.blockSize, 0)
      );

      state.elementSizes.set(element, {
        contentBoxSize,
        borderBoxSize,
        contentRect,
      });
    },
    resize: (
      elements: HTMLElement | HTMLElement[] = [],
      { ignoreImplicit = false } = {}
    ) => {
      if (!Array.isArray(elements)) {
        elements = [elements];
      }

      for (const observer of state.observers) {
        const observedSubset = elements.filter((element) =>
          observer.observationTargets.has(element)
        );

        const observedSubsetAndActive = new Set([
          ...observedSubset,
          ...(ignoreImplicit ? [] : observer.activeTargets),
        ]);

        observer.activeTargets.clear();

        const entries = Array.from(observedSubsetAndActive)
          .map(elementToEntry)
          .filter(Boolean) as ResizeObserverEntry[];

        if (entries.length > 0) {
          observer.callback(entries, observer);
        }
      }
    },
  };
}

const { mockElementSize } = mockResizeObserver();

mockElementSize(document.body, {
  contentBoxSize: [{ blockSize: 100, inlineSize: 100 }],
});

export { mockResizeObserver };
import './DOMRect';

export const mockElementBoundingClientRect = (
  element: HTMLElement,
  {
    x = 0,
    y = 0,
    width = 0,
    height = 0,
  }: Partial<Pick<DOMRect, 'x' | 'y' | 'width' | 'height'>>
) => {
  const savedImplementation = element.getBoundingClientRect;

  element.getBoundingClientRect = jest.fn(
    () => new DOMRectReadOnly(x, y, width, height)
  );

  return savedImplementation;
};
import mediaQuery, { MediaValues } from 'css-mediaquery';
import './MediaQueryListEvent';
import { MockedMediaQueryListEvent } from './MediaQueryListEvent';

/**
 * A tool that allows testing components that use js media queries (matchMedia)
 * `mockViewport` must be called before rendering the component
 * @example using react testing library
 *
 *  const viewport = mockViewport({ width: '320px', height: '568px' })
 *
 *  const { getByText, queryByText } = render(<TestComponent />)
 *
 *  expect(getByText('Content visible only in the phone')).toBeInTheDocument()
 *  expect(queryByText('Content visible only on desktop')).not.toBeInTheDocument()
 *
 *  act(() => {
 *    viewport.set({ width: '1440px', height: '900px' })
 *  })
 *
 *  expect(queryByText('Content visible only on the phone')).not.toBeInTheDocument()
 *  expect(getByText('Content visible only on desktop')).toBeInTheDocument()
 *
 *  viewport.cleanup()
 *
 */

export type ViewportDescription = Partial<MediaValues>;
export type MockViewport = {
  cleanup: () => void;
  set: (newDesc: ViewportDescription) => void;
};

type Listener = (this: MediaQueryList, ev: MockedMediaQueryListEvent) => void;
type ListenerObject = {
  handleEvent: (ev: MockedMediaQueryListEvent) => void;
};
type ListenerOrListenerObject = Listener | ListenerObject;

function isEventListenerObject(
  obj: ListenerOrListenerObject
): obj is ListenerObject {
  return (obj as ListenerObject).handleEvent !== undefined;
}

function mockViewport(desc: ViewportDescription): MockViewport {
  const state: {
    currentDesc: ViewportDescription;
    oldListeners: {
      listener: Listener;
      list: MediaQueryList;
      matches: boolean;
    }[];
    listeners: {
      listener: ListenerOrListenerObject;
      list: MediaQueryList;
      matches: boolean;
    }[];
  } = {
    currentDesc: desc,
    oldListeners: [],
    listeners: [],
  };

  const savedImplementation = window.matchMedia;

  const addOldListener = (
    list: MediaQueryList,
    matches: boolean,
    listener: Listener
  ) => {
    state.oldListeners.push({ listener, matches, list });
  };

  const removeOldListener = (listenerToRemove: Listener) => {
    const index = state.oldListeners.findIndex(
      ({ listener }) => listener === listenerToRemove
    );

    state.oldListeners.splice(index, 1);
  };

  const addListener = (
    list: MediaQueryList,
    matches: boolean,
    listener: ListenerOrListenerObject
  ) => {
    state.listeners.push({ listener, matches, list });
  };

  const removeListener = (listenerToRemove: ListenerOrListenerObject) => {
    const index = state.listeners.findIndex(
      ({ listener }) => listener === listenerToRemove
    );

    state.listeners.splice(index, 1);
  };

  Object.defineProperty(window, 'matchMedia', {
    writable: true,
    value: (query: string): MediaQueryList => ({
      get matches() {
        return mediaQuery.match(query, state.currentDesc);
      },
      media: query,
      onchange: null,
      addListener: function (listener) {
        if (listener) {
          addOldListener(this, this.matches, listener);
        }
      }, // deprecated
      removeListener: (listener) => {
        if (listener) {
          removeOldListener(listener);
        }
      }, // deprecated
      addEventListener: function (
        eventType: Parameters<MediaQueryList['addEventListener']>[0],
        listener: Parameters<MediaQueryList['addEventListener']>[1]
      ) {
        if (eventType === 'change') {
          addListener(this, this.matches, listener);
        }
      },
      removeEventListener: (
        eventType: Parameters<MediaQueryList['removeEventListener']>[0],
        listener: Parameters<MediaQueryList['removeEventListener']>[1]
      ) => {
        if (eventType === 'change') {
          if (isEventListenerObject(listener)) {
            removeListener(listener.handleEvent);
          } else {
            removeListener(listener);
          }
        }
      },
      dispatchEvent: jest.fn(),
    }),
  });

  return {
    cleanup: () => {
      window.matchMedia = savedImplementation;
    },
    set: (newDesc: ViewportDescription) => {
      state.currentDesc = newDesc;
      state.listeners.forEach(({ listener, matches, list }, listenerIndex) => {
        const newMatches = list.matches;

        if (newMatches !== matches) {
          const changeEvent = new MediaQueryListEvent('change', {
            matches: newMatches,
            media: list.media,
          });

          if (isEventListenerObject(listener)) {
            listener.handleEvent(changeEvent);
          } else {
            listener.call(list, changeEvent);
          }

          state.listeners[listenerIndex].matches = newMatches;
        }
      });

      state.oldListeners.forEach(
        ({ listener, matches, list }, listenerIndex) => {
          const newMatches = list.matches;

          if (newMatches !== matches) {
            const changeEvent = new MediaQueryListEvent('change', {
              matches: newMatches,
              media: list.media,
            });

            listener.call(list, changeEvent);

            state.oldListeners[listenerIndex].matches = newMatches;
          }
        }
      );
    },
  };
}

function mockViewportForTestGroup(desc: ViewportDescription) {
  let viewport: MockViewport;

  beforeAll(() => {
    viewport = mockViewport(desc);
  });

  afterAll(() => {
    viewport.cleanup();
  });
}

export { mockViewport, mockViewportForTestGroup };
export class MockedMediaQueryListEvent extends Event {
  readonly matches: boolean;
  readonly media: string;

  constructor(type: 'change', eventInitDict: MediaQueryListEventInit = {}) {
    super(type);

